import { configureStore, createSlice } from "@reduxjs/toolkit";
import { INITIAL_COUNTER_STATE, INITIAL_SECOND_STATE } from "./redux-consts";

const counterSlice = createSlice({
  name: "counter",
  initialState: INITIAL_COUNTER_STATE,
  reducers: {
    increment(state) {
      state.counter++;
    },
    customIncrement(state, action) {
      state.counter += action.payload;
    },
    decrement(state) {
      state.counter--;
    },
    toggleShow(state) {
      state.show = !state.show;
    },
  },
});

const secondSlice = createSlice({
  name: "second",
  initialState: INITIAL_SECOND_STATE,
  reducers: {
    changeName(state) {
      state.counter++;
    },
    toggleAuth(state, action) {
      state.counter += action.payload;
    },
  },
});

export const counterActions = counterSlice.actions;

export const secondActions = secondSlice.actions;

const store = configureStore({
  reducer: [counterSlice.reducer, secondSlice.reducer],
});

export default store;
