import React, { useState } from 'react';
import { Provider } from 'react-redux';

import Counter from './components/Counter';

import simpleStore from "./store/simple-redux";
import toolkitStore from "./store/redux-with-toolkit";


function App() {
  const [isToolkit, setIsToolkit] = useState(false)
  return (
    <Provider store={isToolkit ? toolkitStore : simpleStore}>
      <button onClick={setIsToolkit(prev => !prev)}> {isToolkit ? "Toolkit" : "Vanilla"} </button>
      <Counter />
    </Provider>
  );
}

export default App;
