  }

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>
        {show ? counter : "counter not showing"}
      </div>
      <div>
        <button
          onClick={() => dispatch({ type: "customIncrement", amount: 5 })}
        >
          Super Increment
        </button>
        <button onClick={() => dispatch({ type: "increment" })}>
          Increment
        </button>
        <button onClick={() => dispatch({ type: "decrement" })}>
          Decrement
        </button>
      </div>
      <button onClick={() => dispatch({ type: "toggleShow" })}>
        Toggle Counter
      </button>
    </main>
  );
};

export default Counter;
