import { useSelector, useDispatch, DefaultRootState } from "react-redux";
import { COUNTER_ACTIONS } from "../store/redux-consts";
import { RootState } from "../store/simple-redux";
// import myStore, { counterActions, RootState, secondActions } from "../store/redux-with-toolkit";
import classes from "./Counter.module.css";

const Counter = () => {
  const counter = useSelector((state: RootState) => state.counter);
  const show = useSelector((state: RootState) => state.show);

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>
        {show ? counter : "I am not allowed to show you number"}
      </div>
      <Buttons />
    </main >
  );
};

export default Counter;

// buttons component

const Buttons = () => {
  const dispatch = useDispatch();

  return (
    <>
      <button onClick={() => {
        dispatch({ type: COUNTER_ACTIONS.CUSTOM_INCREMENT, payload: 10 })
      }}>
        Super Increment
      </button>
      <button onClick={() => {
        dispatch({ type: COUNTER_ACTIONS.INCREMENT })
      }}>
        Increment
      </button>
      <button onClick={() => {
        dispatch({ type: COUNTER_ACTIONS.DECREMENT })
      }}>
        Decrement
      </button>
      <button onClick={() => {
        dispatch({ type: COUNTER_ACTIONS.TOGGLE_SHOW })
      }}>
        Toggle Counter
      </button>
    </>
  )
}
