import { useSelector, useDispatch, DefaultRootState } from "react-redux";
import { COUNTER_ACTIONS } from "../store/redux-consts";
import myStore, { counterActions, RootState, secondActions } from "../store/redux-with-toolkit";
import classes from "./Counter.module.css";

const Counter = () => {
  const counter = useSelector((state: RootState) => state.counterSlice.counter);
  const show = useSelector((state: RootState) => state.counterSlice.show);
  const name = useSelector((state: RootState) => state.secondSlice.name);
  const isAuthenticated = useSelector((state: RootState) => state.secondSlice.isAuthenticated);
  console.log('isAuthenticated: ', isAuthenticated);


  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>
        {show ? counter : "counter not showing"}
      </div>
      <div className={classes.value}>
        {name}
        {isAuthenticated}
      </div>
      <Buttons />
    </main >
  );
};

export default Counter;

// buttons component

const Buttons = () => {
  const dispatch = useDispatch();

  const isToolkit = true;

  return (
    <>
      <div>
        <button onClick={() => {
          !isToolkit ? dispatch({ type: COUNTER_ACTIONS.CUSTOM_INCREMENT, payload: 10 }) : dispatch(counterActions.customIncrement(10))
        }}>
          Super Increment
        </button>
        <button onClick={() => {
          !isToolkit ? dispatch({ type: COUNTER_ACTIONS.INCREMENT }) : dispatch(counterActions.increment())
        }}>
          Increment
        </button>
        <button onClick={() => {
          !isToolkit ? dispatch({ type: COUNTER_ACTIONS.DECREMENT }) : dispatch(counterActions.decrement())
        }}>
          Decrement
        </button>
      </div>
      <button onClick={() => {
        !isToolkit ? dispatch({ type: COUNTER_ACTIONS.TOGGLE_SHOW }) : dispatch(counterActions.toggleShow())
      }}>
        Toggle Counter
      </button>
      <div>
        <button onClick={() => {
          dispatch(secondActions.changeName())
        }}>
          name
        </button>
        <button onClick={() => {
          dispatch(secondActions.toggleAuth())
        }}>
          auth
        </button>
      </div>
    </>
  )
}
