import { useSelector, useDispatch } from "react-redux";
import { COUNTER_ACTIONS } from "../store/redux-consts";
import { counterActions } from "../store/redux-with-toolkit";
import classes from "./Counter.module.css";

const Counter = ({ isToolkit }) => {
  const counter = useSelector((state) => state.counter);
  const show = useSelector((state) => state.show);

  // choose one
  const IS_VANILLA_REDUX = false;

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>
        {show ? counter : "counter not showing"}
      </div>
      <Buttons isToolkit={isToolkit} />
    </main >
  );
};

export default Counter;

// buttons component

const Buttons = ({ isToolkit }) => {
  const dispatch = useDispatch();

  return (
    <>
      <div>
        <button onClick={() => {
          isToolkit ? dispatch({ type: COUNTER_ACTIONS.CUSTOM_INCREMENT, payload: 10 }) : dispatch(counterActions.customIncrement(10))
        }}>
          Super Increment
        </button>
        <button onClick={() => {
          isToolkit ? dispatch({ type: COUNTER_ACTIONS.INCREMENT }) : dispatch(counterActions.increment())
        }}>
          Increment
        </button>
        <button onClick={() => {
          isToolkit ? dispatch({ type: COUNTER_ACTIONS.DECREMENT }) : dispatch(counterActions.decrement())
        }}>
          Decrement
        </button>
      </div>
      <button onClick={() => {
        isToolkit ? dispatch({ type: COUNTER_ACTIONS.TOGGLE_SHOW }) : dispatch(counterActions.toggleShow())
      }}>
        Toggle Counter
      </button>
    </>
  )
}
