import { useSelector, useDispatch } from "react-redux";
import { COUNTER_ACTIONS } from "../store/redux-consts";
import classes from "./Counter.module.css";

const Counter = () => {
  const counter = useSelector((state) => state.counter);
  const show = useSelector((state) => state.show);

  // choose one
  const IS_VANILLA_REDUX = true;

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>
        {show ? counter : "counter not showing"}
      </div>
      <Buttons IS_VANILLA_REDUX={IS_VANILLA_REDUX} />
    </main >
  );
};

export default Counter;

const Buttons = ({ IS_VANILLA_REDUX }) => {
  const dispatch = useDispatch();

  const sendDispatch = (actionType, payload) => {
    // payload ? dispatch(actionType, payload && payload) : dispatch(type);
    dispatch(actionType, payload && payload)
  }

  return (
    <>
      <div>
        <button onClick={() => { IS_VANILLA_REDUX ? sendDispatch(COUNTER_ACTIONS.CUSTOM_INCREMENT, 5) : () => { } }}>
          Super Increment
        </button>
        <button onClick={() => { sendDispatch(COUNTER_ACTIONS.INCREMENT) }}>
          Increment
        </button>
        <button onClick={() => { sendDispatch(COUNTER_ACTIONS.DECREMENT) }}>
          Decrement
        </button>
      </div>
      <button onClick={() => { sendDispatch(COUNTER_ACTIONS.TOGGLE_SHOW) }}>
        Toggle Counter
      </button>
    </>
  )
}
